import { render } from '@testing-library/react';
import React from 'react';

class Navbar extends React.Component {
    constructor(props) {
        super(props)
    }
    showHideProfileOptions = (e) => {
        console.log(this.state)
        let cur = this.state.open
        if (cur != 'profile-options open') this.setState({ open: 'profile-options open' })
        else this.setState({ open: 'profile-options' })
    }
    render() {
        return (
            <div>
                <nav>
                    <div class="flex">
                        <img class="nav-logo" src="../../public/img/flipanimv2.png" height="35px" />
                        <a class="linktitle">Browse</a>
                        <a class="linktitle">Search</a>
                        <a class="linktitle">Followed</a>
                    </div>
                    <div class="profile">
                        <img onClick={this.showHideProfileOptions.bind(this.showHideProfileOptions)} />
                    </div>
                </nav>
                <div className={this.state.open}>
                    <p disabled className='logged-in-as'>
                        Logged in as <span>@FlipAnim</span></p>
                    <div class="profile-option" id="profileLink">Profile</div>
                    <div class="profile-option">Settings</div>
                    <div class="profile-option" id="logOut">Log out</div>
                </div>
            </div>
        )
    }

}

export default Navbar
